CREATE TABLE SYSTEMINFO (
    KEYTYPE VARCHAR,
    KEYID   VARCHAR,
    KEYVAL  VARCHAR,
    ISUSE   VARCHAR (1) DEFAULT ('Y')
);


CREATE TABLE USER (
    USERID     VARCHAR (10) NOT NULL,
    USERNAME   VARCHAR (20),
    DEPARTMENT VARCHAR (20),
    DEPGROUP   VARCHAR (20),
    ISOFF      VARCHAR (1)     DEFAULT ('N'),
    EMPLOYEE   INTEGER (1)  DEFAULT (1),
    PRIMARY KEY (
        USERID
    )
);

CREATE TABLE PRIZE (
    PRIZEID     VARCHAR      NOT NULL
                             PRIMARY KEY,
    PRIZE_NAME  VARCHAR (50) NOT NULL,
    PRIZE_LEVEL VARCHAR (1)  NOT NULL,
    TOTAL       INTEGER      NOT NULL
                             DEFAULT (1),
    MEMO        VARCHAR
);


CREATE TABLE PRIZE_LOG (
    USERID  VARCHAR,
    PRIZEID VARCHAR,
    TIMEST  DATETIME,
    STATUS  VARCHAR     DEFAULT ('0'),
    RUNCNT  INTEGER
);


CREATE TABLE BATCHINFO (
    SEQ       INTEGER PRIMARY KEY AUTO_INCREMENT,
    BATCHTYPE VARCHAR,
    FNAME     VARCHAR NOT NULL,
    STATUS    VARCHAR,
    CNT       INTEGER,
    UPTIME    DATE,
    DNTIME    DATE
);






